import { Link, useNavigate } from 'react-router-dom';
import styled from "styled-components";
import { useState, useContext } from 'react';
import axios from 'axios';
import UserContext from '../../contexts/UserContext';

export default function Login() {

    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    const { setUser } = useContext(UserContext);
    const navigator = useNavigate();


    async function handleSubmit(e) {
        e.preventDefault();
        const body = { email, password };
        try {
          const { data } = await axios.post('http://localhost:4000/login', body);


          setUser(data);
          navigator('/rotaAux');
        } catch (error) {
          console.error('Deu erro ao fazer o login');
        }
      }

    return (
        <MainStyle>
        <>
        <h1> Login </h1>

        <form>
            <input
                type="text"
                onChange={e => setEmail(e.target.value)}
                placeholder="Email"
                required
            />
            <input
                type="password"
                onChange={e => setPassword(e.target.value)}
                placeholder="Senha"
                required
            />
            <button type="submit" onClick={handleSubmit}> Entrar </button>
        </form>
        <Link to="/signup">NÃ£o tem uma conta? Cadastre-se agora!</Link>
        </>        
    </MainStyle>
    );
}

const MainStyle = styled.main`
display: flex;
width: 100%;
height: 100vh;
display: flex;
flex-direction: column;
align-items: center;
justify-content: center;

h1 {
    font-family: 'Mark Pro';
    font-style: normal;
    font-weight: bold;
    color: #FFFFFF;
    font-size: 32px;
    line-height: 50px;
    margin: -5px 0px 24px;
}

form {
    display: flex;
    flex-direction: column;
    align-items: center;
    width: 100%;
}

input{
    width: 30%;
    max-width: 470px;
    height: 58px;
    padding: 18px 15px;
    border-radius: 30px;
    border: none;
    background-color: #FFFFFF;
    color:#000000;
    font-size: 16px;
    margin-bottom: 13px;
}

button {
    background-color: #FF6E4E;
    border: none;
    width: 30%;
    height: 54px;
    color: #ffffff;
    font-weight: 700;
    font-size: 20px;
    margin-top: 10px;
    margin-bottom: 36px;
    display: flex;
    align-items: center;
    justify-content: center;
    border-radius: 30px;
}
button:hover{
    cursor: pointer;
}

a {
    color: #ffffff;
    text-decoration: none;
    font-weight: 700;
    font-size: 15px;
    line-height: 18px;
}
`;
