import styled from "styled-components";
import { useState } from "react";

export default function Category({ ion, categoria }) {
    const [ selected, setSelected ] = useState(false);

    return(
        <Classe onClick={() => setSelected(true)}>
            <div selected={selected}>
                <ion-icon name={ion}></ion-icon>
            </div>
            <span selected={selected}>{categoria}</span>
        </Classe>
    );
}
const Classe = styled.div`
    margin-right: 15px;
    display: flex;
    flex-direction: column;
    align-items: center;
    div{
        display: flex;
        width: 70px;
        height: 70px;
        border-radius: 50%;
        background-color: ${props => props.selected ? "#FF6E4E" : "#FFFFFF"};
        align-items: center;
        justify-content: center;

    }
    ion-icon {
        font-size: 45px;
        color: ${props => props.selected ? "#FFFFFF" : "#B3B3C3"};
    }
    span {
        font-family: 'Mark Pro', sans-serif;
        font-size: 12px;
        color: ${props => props.selected ? "#FF6E4E" : "#010035"};
    }
`;
